int
is_leap1(int year)
{
	return (!(year%4) && ((year%100) || !(year%400)));
}

int
is_leap2(int year)
{
	if (year%400 == 0)
		return 1;
	if (year%100 == 0)
		return 0;
	if (year%4)
		return 0;
	return 1;
}

